function authFunction(uname, password, $http, $rootScope, success, error) {

    $rootScope.demoMode = false;
  if ($rootScope.middlewareServerUrl == "https://kryptosda.kryptosmobile.com/kryptosds") {
    $rootScope.demoMode = true;
  }


  var defaultSystem = "banner"; //banner,peoplesoft,colleague,    
  var bannerLoginUrl = $rootScope.middlewareServerUrl + "/services/authenticate/login";
  var bannerDataUrl = $rootScope.middlewareServerUrl + "/services/student";

  var psTenantProduct = "HOSTOS/PS";
  var psLoginUrl = $rootScope.middlewareServerUrl + "/authenticate/" + psTenantProduct;
  var psDataUrl = $rootScope.middlewareServerUrl + "/data/" + psTenantProduct;

  var colleagureTenantProduct = "UAPB/COLLEAGUE";
  var colleagueLoginUrl = $rootScope.middlewareServerUrl + "/authenticate/" + colleagureTenantProduct;
  var colleagueDataUrl = $rootScope.middlewareServerUrl; // + colleagureTenantProduct;
  var defaultLoginUrl = bannerLoginUrl;
  var defaultDataUrl = bannerDataUrl;


  console.log("printing rootScope");
  console.log($rootScope);
  try {
    var authSuccess = success;
    var authError = error;

    //Banner Authentication with LDAP - middlware auth

    var url = defaultLoginUrl;
    var data = {};
    if (window.device) {
      data = "username=" + uname + "&password=" + encodeURIComponent(password);
    } else {
      url = "/websimulator/json?url=" + url;
      data = {
        method: "POST",
        body: "username=" + uname + "&password=" + encodeURIComponent(password)
      };
    }
    var authUrl = url;
    var authData = data;
    $rootScope.generateST = function(tgt, service, callback) {
      //Bypassing the ST as there is no service ticket required here..
      callback(tgt);
    };

   

    $rootScope.postAPI = function(endpoint, postdata, callback) {
      console.log(endpoint);
      $.blockUI({
        message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
      });
      $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
        var serviceurl = defaultDataUrl + endpoint + "?ticket=" + st;
        console.log(serviceurl);
        var url = "";
        var proxyMethod = "POST";
        var proxyData = postdata;
        if (window.device) {
          url = serviceurl;
        } else {
          url = "/websimulator/json?url=" + serviceurl;
          proxyMethod = "POST";
          proxyData = {
            method: "POST",
            body: postdata
          }
        }
        /**************************************************************
          Demo API handler code: START
          The below code in this section should be removed in the PROD environment
        **************************************************************/
        if ($rootScope.demoMode) {
          proxyData = "";
          url = serviceurl;
        }
        /**************************************************************
          Demo API handler code: END
        **************************************************************/
        $http({
          method: proxyMethod,
          url: url,
          data: proxyData
        }).
        success(function(data, status, headers, config) {
          $.unblockUI();
          callback(data, status, headers, config);
        }).
        error(function(data, status, headers, config) {
          //$.unblockUI();
          //alert("Error in service call : " + data + " Status : " + status);
          if (status == 403) { //Ticket expired.. need to relogin
            $http.post(authUrl, authData).success(function(data, status, headers, config) {

              if (!data.error) {
                $rootScope.ticket = data.ticket;
                if (defaultSystem == 'banner') {
                  $rootScope.getAPI(defaultDataUrl + "/banneruserroles", function(roles) {
                    if (roles && roles.getuserroles && roles.getuserroles[0]) {
                      var uroles = [];
                      if (roles.getuserroles[0].STUDENT == "Y") {
                        uroles.push("STUDENT");
                      }
                      if (roles.getuserroles[0].EMPLOYEE == "Y") {
                        uroles.push("EMPLOYEE");
                      }
                      if (roles.getuserroles[0].FACULTY == "Y") {
                        uroles.push("FACULTY");
                      }
                      if (roles.getuserroles[0].GOVROLE_ALUMNI_IND == "Y") {
                        uroles.push("ALUMNI");
                      }
                      if (roles.getuserroles[0].GOVROLE_FINANCE_IND == "Y") {
                        uroles.push("FINANCE");
                      }
                      if (roles.getuserroles[0].FINAID == "Y") {
                        uroles.push("FINAID");
                      }
                      data.roles = uroles;
                    }
                    console.log(data);
                    authSuccess(data);
                  });
                } else {
                  authSuccess(data);
                }

              } else {
                authSuccess(data);
              }
              //authSuccess(data);
              $rootScope.postAPI(endpoint, postdata, callback);
            }).error(function(data, status, headers, config) {
              authError(data);

            });
          }

        });

      });

    };

    $rootScope.getAPI = function(endpoint, callback) {
      /**************************************************************
        Demo API handler code: START
        The below code in this section should be removed in the PROD environment
      **************************************************************/
      if ($rootScope.demoMode) {
        console.log("endpoint:before:" + endpoint);
        var tempParamExists = endpoint.indexOf("=");
        var tempUrl = "";
        if (tempParamExists > 0) {
          endpoint = endpoint.substring(0, endpoint.lastIndexOf("/"));
        }
        console.log("new end point");
        console.log(endpoint);
      }
      /*************************************************************
        Demo API Handler Code: END
      *************************************************************/
      $.blockUI({
        message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
      });
      $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
        var serviceurl = defaultDataUrl + endpoint + "?ticket=" + st;
