function pageprocessorHoldsB1(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
	console.log($routeParams);

    $rootScope.getAPI(pagedef.endpoint, function(data) {
            $scope.hold = data.hold;        
    });
}

function pageprocessorMidTermGradeB1(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $scope.selterm = "";
  $scope.midterm = [];
  $scope.studentInfo = [];

  $rootScope.getAPI( pagedef.endpoint, function(data) {
    $scope.gradesdropdown = data.dropdown;
  });

  $scope.termselect = function(selterm) {
    $scope.midterm = [];
    $('#termsel').blur();
    if (!$scope.selterm) {
      $scope.midterm = [];
      $scope.studentInfo = [];
      return;
    }
    $rootScope.getAPI( "/bannermidtermgrade/termCode=" + $scope.selterm, function(data) {
      
      var tempColl = [];
      angular.forEach(data.midterm, function(value, key) {
        if (value.MIDTERM_GRADE) {
          tempColl.push(value);
        }
      });
      data.midterm = tempColl;
      $scope.midterm = data.midterm;
      $scope.studentInfo = data.studentInfo;
    });
  }
}

function pageprocessorFinalGradeB1(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $scope.selterm = "";
  $scope.studentInfo = [];
  $scope.finalgrades = [];
  $scope.currentsummary = [];
  $scope.cumulativesummary = [];
  $scope.transfersummary = [];
  $scope.overallsummary = [];

  $.blockUI();

  $rootScope.getAPI( pagedef.endpoint, function(data) {
    $scope.gradesdropdown = data.dropdown;
    if (data.dropdown.length == 1) {
      $scope.selterm = data.dropdown[0].TERM_CODE;
      $scope.termselect($scope.selterm);
    }
  });


  $scope.termselect = function(termcode) {
    $.blockUI();
    $('#termsel').blur();


    $rootScope.getAPI( "/bannerfinalgrade/termCode=" + $scope.selterm, function(data) {
      var tempColl = [];
      angular.forEach(data.finalgrade, function(value, key) {
        if (value.FINAL_GRADE) {
          tempColl.push(value);
        }
      });
      data.finalgrade = tempColl;
      $scope.studentInfo = data.studentInfo;
      $scope.finalgrades = data.finalgrade;
      $scope.currentsummary = data["summary.current"];
      $scope.cumulativesummary = data["summary.cumulative"];
      $scope.transfersummary = data["summary.transfer"];
      $scope.overallsummary = data["summary.overall"];
    });

  }
}

function pageprocessorACCSummary(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $scope.listdata = [];
  $scope.showsummary = false;


  $rootScope.getAPI( pagedef.dropdownendpoint, function(data) {
    $scope.dropdown = data;
  });

  $scope.dropdownselect = function(selectedvalue) {
    $scope.listdata = null;
    $('#termselect').blur();
    if (!$scope.selectedvalue) {
      $scope.showsummary = false;
      return;
    }

    $rootScope.getAPI( pagedef.listendpoint + $scope.selectedvalue, function(data) {
