  $scope.addToCart = function(crn) {
    angular.forEach($scope.classes, function(value, key) {
      if (value.CRN == crn) {
        if (value.OLR_COURSE == 'Y') {
          if (!value.START_DATE && !value.END_DATE) {
            if (window.device) {
              navigator.notification.alert("Start Date or End Date should be mentioned for OLR courses.", null, "Add/Drop Classes", null);
            } else {
              apprise("Start Date or End Date should be mentioned for OLR courses.", {
                'verify': false,
                'textYes': "Ok"
              }, function(r) {

              });
            }
            return;
          }
          if (value.START_DATE && value.END_DATE) {
            if (window.device) {
              navigator.notification.alert("Only one of start or end dates may be entered.", null, "Add/Drop Classes", null);
            } else {
              apprise("Only one of start or end dates may be entered.", {
                'verify': false,
                'textYes': "Ok"
              }, function(r) {

              });
            }
            return;
          }
          var cStartDate, cEndDate;
          if (value.START_DATE) {
            var dStartDate = new Date(value.START_DATE);
            cStartDate = dStartDate.getDate() + "-" + monthName[dStartDate.getMonth()] + "-" + dStartDate.getFullYear();
          }
          if (value.END_DATE) {
            var dEndDate = new Date(value.END_DATE);
            cEndDate = dEndDate.getDate() + "-" + monthName[dEndDate.getMonth()] + "-" + dEndDate.getFullYear();
          }
        }

        value.addedToCart = true;
        value.formatClass = "in-cart-course";
        $scope.cart.push(value);
        console.log($scope.cart);
        return;
      }
    });

  }

  $scope.removeFromCart = function(crn) {
    for (var i = 0; i <= $scope.cart.length - 1; i++) {
      if ($scope.cart[i].CRN === crn) {
        $scope.cart.splice(i, 1);
      }
    }
    angular.forEach($scope.classes, function(value, key) {
      if (value.CRN == crn) {
        value.addedToCart = false;
        value.formatClass = "";
        console.log($scope.cart);
      }
    });
    console.log($scope.cart);
  }

  $scope.submitCart = function() {
    if ($scope.cart.length == 0) {
      if (window.device) {
        navigator.notification.alert("No classes added to cart.", null, "Add/Drop Classes", null);
      } else {
        apprise("No classes added to cart.", {
          'verify': false,
          'textYes': "Ok"
        }, function(r) {

        });
      }
      return;
    }
    //build param map for registration service
    var endpoint = "/banneraddDropClasses";
    var postdata = "";
    var termCode = "";
    var crns = "";
    var regCodes = "";
    var action = "A";
    var startDates = "";
    var endDates = "";
    var firstTime = true;
    $scope.regResults = "";

    angular.forEach($scope.cart, function(value, key) {
      termCode = $scope.selectedvalue;
      if (!firstTime) {
        crns = crns + ",";
        regCodes = regCodes + ",";
        startDates = startDates + ",";
        endDates = endDates + ",";
      }
      crns = crns + value.CRN;
      regCodes = regCodes + "RB";
      if (value.START_DATE) {
        var dStartDate = new Date(value.START_DATE);
        cStartDate = (dStartDate.getDate() + 1) + "-" + monthName[dStartDate.getMonth()] + "-" + dStartDate.getFullYear();
        startDates = startDates + cStartDate;
      }
      if (value.END_DATE) {
        var dEndDate = new Date(value.END_DATE);
        cEndDate = (dEndDate.getDate() + 1) + "-" + monthName[dEndDate.getMonth()] + "-" + dEndDate.getFullYear();
        endDates = endDates + cEndDate;
      }
      firstTime = false;
    });
    postdata = "termCode=" + termCode + "&crns=" + crns + "&regCodes=" + regCodes + "&action=" + action + "&startDates=" + startDates + "&endDates=" + endDates;

    $rootScope.postAPI(endpoint, postdata, function(data) {

      if (window.device) {
        var dres = JSON.parse(data.p_out[0].summary);
        data.p_out[0].summary = dres;
      }
      $scope.regResults = data.p_out[0];
      if ($scope.regResults.summary && $scope.regResults.summary.results) {
        angular.forEach($scope.regResults.summary.results, function(value, key) {
          if (value.status == 'S') {
            value.CSS_CLASS = "alert-success";
          } else if (value.status == 'E') {
            value.CSS_CLASS = "alert-danger";
          } else {
            value.CSS_CLASS = "alert-warning";
          }
        });
      }
      $scope.showmessage = true;
      $scope.clearCart();
      $scope.refreshCurrentSchedule();
    });

  }

  $scope.showResults = function() {
    if ($scope.selectedvalue == "") {
      if (window.device) {
        navigator.notification.alert("Please select term code.", null, "Add/Drop Classes", null);
      } else {
        apprise("Please select term code", {
          'verify': false,
          'textYes': "Ok"
        }, function(r) {

        });
      }
      return;
    }
    if ($scope.selectedSubject == "" && $scope.selectedInstructor == "") {
      if (window.device) {
        navigator.notification.alert("Please select Subject or Instructor.", null, "Add/Drop Classes", null);
      } else {
        apprise("Please select Subject or Instructor.", {
          'verify': false,
          'textYes': "Ok"
        }, function(r) {

        });
      }
      return;
    }

    $scope.showmessage = false;
    var mon, tue, wed, thu, fri, sat, sunday;
    mon = ($scope.weekdata[0].selected == 'Y') ? 'M' : '';
    tue = ($scope.weekdata[1].selected == 'Y') ? 'T' : '';
    wed = ($scope.weekdata[2].selected == 'Y') ? 'W' : '';
    thu = ($scope.weekdata[3].selected == 'Y') ? 'R' : '';
    fri = ($scope.weekdata[4].selected == 'Y') ? 'F' : '';
    sat = ($scope.weekdata[5].selected == 'Y') ? 'S' : '';
    sunday = ($scope.weekdata[6].selected == 'Y') ? 'U' : '';

    var searchURL = "/bannerregCartclasslookup/termCode=" + $scope.selectedvalue +
      "&subjectCode=" + $scope.selectedSubject +
      "&courseNumber=" + $scope.courseNumber +
      "&partTermCode=" + $scope.partTermCode +
      "&facultyPidm=" + $scope.selectedInstructor +
      "&courseTitle=" + $scope.courseTitle +
      "&creditFrom=" + $scope.creditFrom +
      "&creditTo=" + $scope.creditTo +
      "&campCode=" + $scope.selectedCampus +
      "&mon=" + mon +
      "&tue=" + tue +
      "&wed=" + wed +
      "&thu=" + thu +
      "&fri=" + fri +
      "&sat=" + sat +
      "&sun=" + sunday;
    $rootScope.getAPI(searchURL, function(data) {
      if (data.CLASSLOOKUP.length == 0) {
        if (window.device) {
          navigator.notification.alert("No classes found.", null, "Add/Drop Classes", null);
        } else {
          apprise("No classes found.", {
            'verify': false,
            'textYes': "Ok"
          }, function(r) {

          });
        }
      } else {
        console.log(data.CLASSLOOKUP);
        $scope.classes = [];
        $scope.inSearchWindow = false;
        var prevValue = "";
        var vindex = 0;
        angular.forEach(data.CLASSLOOKUP, function(value, key) {
          if (value[0].TIME && value[0].TIME.length > 8) {
            value[0].TIME_DISPLAY = value[0].SSRMEET_START_TIME + " - " + value[0].SSRMEET_END_TIME;
          } else {
            value[0].TIME_DISPLAY = "TBA";
          }

          value[0].START_DATE = "";
          value[0].END_DATE = "";
          value[0].ONLINE_COURSE = false;
          if ((value[0].SSRMEET_START_TIME == '' || value[0].SSRMEET_START_TIME == null) &&
            (value[0].SSRMEET_END_TIME == '' || value[0].SSRMEET_END_TIME == null) &&
            (value[0].TIME == "TBA")) {
            value[0].ONLINE_COURSE = true;
          }

          var days = value[0].TIME.indexOf(" ") != -1 ? value[0].TIME.split(" ")[0] : "";
          value[0].WEEK_DATA = [{
            "LABEL": "Mon",
            "AVAIL": days.indexOf("M") != -1,
            "DISP_CLASS": days.indexOf("M") != -1 ? "btn-success" : "btn-primary"
          }, {
            "LABEL": "Tue",
            "AVAIL": days.indexOf("T") != -1,
            "DISP_CLASS": days.indexOf("T") != -1 ? "btn-success" : "btn-primary"
          }, {
            "LABEL": "Wed",
            "AVAIL": days.indexOf("W") != -1,
            "DISP_CLASS": days.indexOf("W") != -1 ? "btn-success" : "btn-primary"
          }, {
            "LABEL": "Thu",
            "AVAIL": days.indexOf("R") != -1,
            "DISP_CLASS": days.indexOf("R") != -1 ? "btn-success" : "btn-primary"
          }, {
            "LABEL": "Fri",
            "AVAIL": days.indexOf("F") != -1,
            "DISP_CLASS": days.indexOf("F") != -1 ? "btn-success" : "btn-primary"
          }, {
            "LABEL": "Sat",
            "AVAIL": days.indexOf("S") != -1,
            "DISP_CLASS": days.indexOf("S") != -1 ? "btn-success" : "btn-primary"
          }, {
            "LABEL": "Sun",
            "AVAIL": days.indexOf("U") != -1,
            "DISP_CLASS": days.indexOf("U") != -1 ? "btn-success" : "btn-primary"
          }];
          if (!value[0].schedule) {
            value[0].schedule = [];
          }
          value[0].schedule.push({
            "DURATION": value[0].DURATION,
            "INSTRUCTOR": value[0].INSTRUCTOR,
            "TIME_DISPLAY": value[0].TIME_DISPLAY,
            "TIME": value[0].TIME,
            "LOCATION": value[0].LOCATION,
            "WEEK_DATA": value[0].WEEK_DATA
          });
          value[0].addedToCart = false;
          value[0].formatClass = "";
          value[0].REGISTERED = false;
          angular.forEach($scope.currentSchedule, function(schvalue, schkey) {
            if (schvalue.SFTREGS_CRN == value[0].CRN) {
              value[0].REGISTERED = true;
              //return;
            }
          });
          //
          if (prevValue == "") {
            $scope.classes.push(value[0]);
            prevValue = value[0];
          } else if (prevValue.CRN == value[0].CRN &&
            //prevValue.TERM_CODE == value[0].TERM_CODE &&
            //prevValue.SUBJECT_CODE == value[0].SUBJECT_CODE &&
            prevValue.CREDIT_HOURS == value[0].CREDIT_HOURS &&
            prevValue.COURSE_ID == value[0].COURSE_ID) {
            $scope.classes[vindex].schedule.push(value[0].schedule[0]);
          } else {
            vindex++;
            $scope.classes.push(value[0]);
            prevValue = value[0];
          }

        });
      }
    });

  }

  $scope.submitDrops = function() {
    if ($scope.currentSchedule.length == 0) {
      if (window.device) {
        navigator.notification.alert("No class registrations found !", null, "Add/Drop Classes", null);
