    var regCodes = "";
    var action = "D";
    var startDates = "";
    var endDates = "";
    var firstTime = true;
    $scope.regResults = "";

    angular.forEach($scope.currentSchedule, function(value, key) {
      termCode = $scope.selectedvalue;
      if (value.action) {
        if (!firstTime) {
          crns = crns + ",";
          regCodes = regCodes + ",";
          startDates = startDates + ",";
          endDates = endDates + ",";
        }
        crns = crns + value.SFTREGS_CRN;
        regCodes = regCodes + value.action;
        if (value.SFTREGS_START_DATE) {
          startDates = startDates + value.SFTREGS_START_DATE;
        }
        if (value.SFTREGS_COMPLETION_DATE) {
          endDates = endDates + value.SFTREGS_COMPLETION_DATE;
        }
        firstTime = false;
      }

    });
    if (firstTime) {
      if (window.device) {
        navigator.notification.alert("Please select an action for atleast one class !", null, "Add/Drop Classes", null);
      } else {
        apprise("Please select an action for atleast one class !", {
          'verify': false,
          'textYes': "Ok"
        }, function(r) {

        });
      }
      return;
    }
    postdata = "termCode=" + termCode + "&crns=" + crns + "&regCodes=" + regCodes + "&action=" + action + "&startDates=" + startDates + "&endDates=" + endDates;
    $rootScope.postAPI(endpoint, postdata, function(data) {
      console.log(data);
      if (window.device) {
        var dres = JSON.parse(data.p_out[0].summary);
        data.p_out[0].summary = dres;
      }
      $scope.regResults2 = data.p_out[0];
      if ($scope.regResults2.summary && $scope.regResults2.summary.results) {
        angular.forEach($scope.regResults2.summary.results, function(value, key) {
          if (value.status == 'S') {
            value.CSS_CLASS = "alert-success";
          } else if (value.status == 'E') {
            value.CSS_CLASS = "alert-danger";
          } else {
            value.CSS_CLASS = "alert-warning";
          }
        });
      }
      $scope.showmessage2 = true;
      $scope.inSearchWindow = true;
      //$scope.refreshCurrentSchedule(); 
      //$scope.clearCart();
    });
  }



