function pageprocessorLookupClasses(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $scope.selectedSubject = "";
  $scope.courseNumber = "";
  $scope.partTermCode = "";
  $scope.selectedInstructor = "";
  $scope.courseTitle = "";
  $scope.creditFrom = "";
  $scope.creditTo = "";
  $scope.selectedvalue = "";

  var termdropdownURL = "/bannertermsForClassLookup";

  $rootScope.getAPI( termdropdownURL, function(data) {
    $scope.termdropdown = data.termdropdown;
  });

  $scope.dropdownselect = function(selectedvalue) {
    $('#termselect').blur();
    var otherSearchParamsURL = "/bannergetOtherClassSearchParams/termCode=";

    $rootScope.getAPI( otherSearchParamsURL + $scope.selectedvalue, function(data) {
      $scope.listdata = data;
    });
  }

  $scope.search = function() {
    if ($scope.selectedvalue == "") {
      if (window.device) {
        navigator.notification.alert("Please select term code.", null, "Look Up Classes", null);
      } else {
        apprise("Please select term code", {
          'verify': false,
          'textYes': "Ok"
        }, function(r) {

        });
      }
      return;
    }
    if ($scope.selectedSubject == "" && $scope.selectedInstructor == "") {
      if (window.device) {
        navigator.notification.alert("Please select Subject or Instructor.", null, "Look Up Classes", null);
      } else {
        apprise("Please select Subject or Instructor.", {
          'verify': false,
          'textYes': "Ok"
        }, function(r) {

        });
      }
      return;
    }
    var searchURL = "/bannerclasslookup/termCode=" + $scope.selectedvalue +
      "&subjectCode=" + $scope.selectedSubject +
      "&courseNumber=" + $scope.courseNumber +
      "&partTermCode=" + $scope.partTermCode +
      "&facultyPidm=" + $scope.selectedInstructor +
      "&courseTitle=" + $scope.courseTitle +
      "&creditFrom=" + $scope.creditFrom +
      "&creditTo=" + $scope.creditTo;

    $rootScope.getAPI( searchURL, function(data) {
      if (data.CLASSLOOKUP.length == 0) {
        if (window.device) {
          navigator.notification.alert("No record found.", null, "Look Up Classes", null);
        } else {
          apprise("No record found.", {
            'verify': false,
            'textYes': "Ok"
          }, function(r) {

          });
        }
      } else {
        $rootScope.classlookup = data.CLASSLOOKUP;
        $location.path("/app/LookupClasses/LookupClassesDetails");
      }
    });
  }
}

function pageprocessorFacebook317(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  if (window.device) {
    $.blockUI();
  }
  var iabRef = window.open(pagedef.url, "_blank", "location=no,hidden=yes,enableViewportScale=yes,clearcache=yes");
  var hideBlockUi = function() {
    $.unblockUI();
    iabRef.show();
  };
  var iabClose = function(data) {
    $.unblockUI();
    iabRef.removeEventListener("exit", iabClose);
    window.location.href = "index.html#home";
  };
  iabRef.addEventListener("exit", iabClose);
  var loadStop = function(data) {
    hideBlockUi();
    iabRef.removeEventListener("loadstop", loadStop);
  };
  iabRef.addEventListener("loadstop", loadStop);
}

